import React from "react"
import translate from "./translate";


class Main extends React.Component{
    constructor() {
        super();
        this.state = {
            textAreaValue: "",
            run: false,
            output: "",
            error:""
        };
        this.handleChange = this.handleChange.bind(this);
        this.run = this.run.bind(this)
    }

    handleChange(event) {
        this.setState({ textAreaValue: event.target.value });
    }

    run() {
        let code = this.state.textAreaValue
        console.log(code)

        // Turning this into a function
        code = "function jsfjdnfs() {\n let jsdfksdj=[]\n"+code+"\n return jsdfksdj\n} \njsfjdnfs()"

        let js = translate(code)
        console.log(js)

        // Running string
        try {
            let out = eval(js)

            let outp = ""
            for (let q=0; q<out.length;q++) {
                outp = outp+out[q].toString()+"\n"
            }
            this.setState({ error: " " })
            this.setState({ output: outp })
        } catch (error) {
            console.log(error)
            let err = error.name
            console.log(err)
            this.setState({ output:" " })
            if (err=="ReferenceError") {
                this.setState({ error: "Error: "+(error.message).toString()+". \nHint: you may have to write 'let' the first time that you define a variable. eg. 'let x = 3'"});
                //Error: varmake sure to write 'let' the first time that you define a variable. \neg. 'let x = 3'
            } else if (err=="SyntaxError") {
                let m=error.message
                if (m.indexOf("end of input") != -1) {
                    this.setState({ error:"Error: remember to end if statements, loops and functions with 'end'."})
                } 

            } else {
                this.setState({ error:"Error: "+(error.message).toString()})
            }
        }


    }

    render(){
        return(
            <div className="h-screen">
                <div className="navigation flex m-5">
                    <img src="https://via.placeholder.com/150x60.png" alt="" className="logo" />
                    <div className="w-96 flex-1"></div>
                    <button className="border-green-500 border-2 hover:bg-white bg-green-500 py-3 px-8 rounded-full font-bold text-white hover:text-green-500" onClick={this.run}>Run</button>
                </div>
                <form className="body-div flex">    
                    <textarea value={this.state.textAreaValue} onChange={this.handleChange} placeholder="Enter your code here." className=" border-2 border-black m-10 flex-1 h-full"/>

                    <div className="flex-1 border-2 border-black m-10">
                        <pre>{this.state.output}</pre>
                        <div className="text-red-500 break-words">{this.state.error}</div>
                    </div>
                </form>
            </div>
        )
    }
}

export default Main
