import React from "react"

class Main extends React.Component{
    constructor() {
        super();
        this.state = {
            name:"",
            temp_choice:""
        };
        this.handleChange = this.handleChange.bind(this);
        this.create = this.create.bind(this)
        this.choose = this.choose.bind(this)
        this.temp_select = this.temp_select.bind(this)
    }

    create(){
        console.log(this.state.name)

        // maybe add entry to firebase
    }

    handleChange(event) {
        this.setState({ name: event.target.value });
    }

    choose(){
        let temp = this.state.temp_choice
        this.setState({ name: temp })
    }

    temp_select(event){
        this.setState({ temp_choice: event.target.value });
        console.log("temp select ",event.target.value)
    }

    render(){
        return(
            <div>
                <p>Creating a new profile:</p>
                <div>
                    <input type="text" placeholder="Enter your name here." onChange={this.handleChange}/>
                    <button onClick={this.create} className="">Add</button>
                </div>
                <p>Or choose a previously created one:</p>
                <div>
                    <select name="" id="" onChange={this.temp_select}>
                        <option value="default">--Select a name --</option>
                        <option value="volvo">Volvo</option>
                        <option value="saab">Saab</option>
                        <option value="mercedes">Mercedes</option>
                        <option value="audi">Audi</option>
                    </select>
                    <button onClick={this.choose} className="">Add</button>
                </div>
                <button onClick={this.create} className="">Test</button>
            </div>
        )
    }
}

export default Main
