import React from "react"

class Main extends React.Component{
    constructor() {
        super();
        this.state = {
            name:"",
            temp_choice:""
        };
        this.handleChange = this.handleChange.bind(this);
        this.create = this.create.bind(this)
        this.choose = this.choose.bind(this)
        this.temp_select = this.temp_select.bind(this)
    }

    create(){
        console.log(this.state.name)

        // maybe add entry to firebase
    }

    handleChange(event) {
        this.setState({ name: event.target.value });
    }

    choose(){
        let temp = this.state.temp_choice
        this.setState({ name: temp })
    }

    temp_select(event){
        this.setState({ temp_choice: event.target.value });
    }

    render(){
        return(
            <div className="w-screen flex h-screen">
                <div className="m-auto w-1/2 h-3/5">
                    <p className="mx-5 text-lg font-bold text-green-500">User: <span className="text-black italic">{this.state.name=="" ? "not chosen" : this.state.name}</span></p>
                    <div className=" border-2 p-10 m-5 w-full">
                        <p className="m-auto font-bold">Creating a new profile:</p>
                        <hr className="border-t-1 w-full mt-2 mb-4"></hr>
                        <div className="m-auto">
                            <input type="text" placeholder="Enter your name here." onChange={this.handleChange} className="mr-3 outline-none"/>
                            <button onClick={this.create} className="text-green-500 font-bold">Add</button>
                        </div>
                    </div>
                    <div className=" border-2 p-10 m-5 w-full">
                        <p className="m-auto font-bold">Or choose a previously created one:</p>
                        <hr className="border-t-1 w-full mt-2 mb-4"></hr>
                        <div className="m-auto">
                            <select name="" id="" onChange={this.temp_select} className="mr-3 outline-white" placeholder="Select a name">

                                <option value="Person 1">Person 1</option>
                                <option value="Person 2">Person 2</option>
                                <option value="Person 3">Person 3</option>
                                <option value="Person 4">Person 4</option>
                            </select>
                            <button onClick={this.choose} className="text-green-500 font-bold">Choose</button>
                        </div>
                    </div>
                    <div>
                        <button onClick={this.create} className="py-3 border-2 border-green-500 rounded-lg mx-5 inline-block w-full bg-green-500 text-white font-bold hover:bg-green-400 hover:border-green-400" type="button" >GO!</button>
                    </div>

                </div>

            </div>
        )
    }
}

export default Main
