import './App.css';
import React from "react"
import Authenticate from "./Pages/main.js"
import TypePage from './Pages/typing';
import Landing from './Pages/landing';

class App extends React.Component{
      constructor(props) {
            super(props)

            this.state = {
                  ready: false,
                  loggedIn : false,
                  profile: "",
            }

            this.authHandler = this.authHandler.bind(this)
            this.backHandler = this.backHandler.bind(this)
            this.readyHandler = this.readyHandler.bind(this)
      }

      authHandler(name) {
            this.setState({ loggedIn: true })
            this.setState({ profile:name })
      }

      backHandler() {
            this.setState({ loggedIn: false })
            this.setState({ profile:"" })
      }

      readyHandler() {
            this.setState({ ready: true })
      }

      render(){
            if (this.state.loggedIn) {
                  return(
                        <TypePage profile={this.state.profile} handler={this.backHandler}/>

                  )

            } else if (this.state.ready) {
                  return(
                        <Authenticate handler={this.authHandler}/>
                  )

            } else {
                  return(
                        <div className='w-screen h-screen'>
                              <Landing handler={this.readyHandler}/>
                              <div className='flex'>
                                    <div className="w-96 flex-1"></div>
                                    <p>Copyright Leo Strijbos</p>
                              </div>
                        </div>

                  )
            }

      }
}





export default App;
