import app from "firebase/compat/app";
import "firebase/auth";

const firebaseConfig = {
    apiKey: "AIzaSyCEKBaH3-dKe49nWHQuzOM_sN94dZpbE80",
    authDomain: "scamprotection-369b9.firebaseapp.com",
    projectId: "scamprotection-369b9",
    storageBucket: "scamprotection-369b9.appspot.com",
    messagingSenderId: "1067736123828",
    appId: "1:1067736123828:web:b6b8fe440c52e6d0478ae1"
};

class Firebase{
    constructor() {
        this.app = app.initializeApp(firebaseConfig);
    }

    signUserIn = async() => {
        const provider = new app.auth.TwitterAuthProvider();
        app
        .auth()
        .signInWithPopup(provider);
    }
}

export default Firebase;
