import './App.css';
import React from "react"
import Authenticate from "./Pages/main.js"
import TypePage from './Pages/typing';

class App extends React.Component{
      constructor(props) {
            super(props)

            this.state = {
                  loggedIn : false,
                  profile: ""
            }

            this.authHandler = this.authHandler.bind(this)
            this.backHandler = this.backHandler.bind(this)
      }

      authHandler(name) {
            this.setState({ loggedIn: true })
            this.setState({ profile:name })
      }

      backHandler() {
            this.setState({ loggedIn: false })
            this.setState({ profile:"" })
      }

      render(){
            return(
                  <div className=''>
                        {
                              this.state.loggedIn ?  <TypePage profile={this.state.profile} handler={this.backHandler}/> : <Authenticate handler={this.authHandler}/>
                        }

                  </div>

            )
      }
}





export default App;
