import React from "react"
import translate from "./translate";


class Main extends React.Component{
    constructor() {
        super();
        this.state = {
            textAreaValue: "",
            run: false,
            output: "",
            error:""
        };
        this.handleChange = this.handleChange.bind(this);
        this.run = this.run.bind(this)
    }

    handleChange(event) {
        this.setState({ textAreaValue: event.target.value });
    }

    run() {
        let code = this.state.textAreaValue
        console.log(code)

        // Turning this into a function
        code = "function jsfjdnfs() {\n jsdfksdj=[]\n"+code+"\n return jsdfksdj\n} \njsfjdnfs()"

        let js = translate(code)
        console.log(js)

        // Running string
        try {
            out = eval(js)
        } catch (error) {
            console.log(typeof(error))
            console.log(error)
            let err = error.name
            console.log(err)
            if (err=="ReferenceError") {
                this.setState({ error: "Error: make sure to write 'let' the first time that you define a variable. \neg. 'let x = 3'"});
                console.log("Error: make sure to write 'let' the first time that you define a variable. \neg. 'let x = 3'")
            }
        }

        // this.setState({output:log})
    }

    // using regex to look for patterns
    render(){
        return(
            <div>
                <div className="navigation flex">
                    <img src="https://via.placeholder.com/150x60.png" alt="" className="logo" />
                    <div className="w-96 flex-1"></div>
                    <button className="" onClick={this.run}>Run</button>
                </div>
                <form className="body-div flex">    
                    <textarea value={this.state.textAreaValue} onChange={this.handleChange} placeholder="Enter your code here." className="w-96 h-96 border-2 border-black m-auto"/>

                    <div className="w-96 h-96 border-2 border-black m-auto">
                        <p>{this.state.output}</p>
                        <p className="text-red-500">{this.state.error}</p>
                    </div>
                </form>
            </div>
        )
    }
}

export default Main
