import React from "react"

class Main extends React.Component{
    constructor() {
        super();
        this.state = {
            name:"",
            temp_choice:""
        };
        this.handleChange = this.handleChange.bind(this);
        this.create = this.create.bind(this)
        this.choose = this.choose.bind(this)
        this.temp_select = this.temp_select.bind(this)
    }

    create(){
        console.log(this.state.name)

        // maybe add entry to firebase
    }

    handleChange(event) {
        this.setState({ name: event.target.value });
    }

    choose(){
        let temp = this.state.temp_choice
        this.setState({ name: temp })
    }

    temp_select(event){
        this.setState({ temp_choice: event.target.value });
    }

    render(){
        return(
            <div className="w-screen flex h-screen">
                <div className="m-auto w-1/2 h-3/5">
                    <div className=" border-2 p-10 m-5 w-full">
                        <p className="m-auto">Creating a new profile:</p>
                        <div className="m-auto">
                            <input type="text" placeholder="Enter your name here." onChange={this.handleChange}/>
                            <button onClick={this.create} className="">Add</button>
                        </div>
                    </div>
                    <div className=" border-2 p-10 m-5 w-full">
                        <p className="m-auto">Or choose a previously created one:</p>
                        <div className="m-auto">
                            <select name="" id="" onChange={this.temp_select}>
                                <option value="default">--Select a name --</option>
                                <option value="volvo">Person 1</option>
                                <option value="saab">Person 2</option>
                                <option value="mercedes">Person 3</option>
                                <option value="audi">Person 4</option>
                            </select>
                            <button onClick={this.choose} className="">Add</button>
                        </div>
                    </div>
                    <div>
                        <button onClick={this.create} className="py-3 border-2 border-green-500 rounded-lg mx-5 inline-block w-full bg-green-500 text-white font-bold" type="button" >GO!</button>
                    </div>

                </div>

            </div>
        )
    }
}

export default Main
