import React from "react"

class TypePage extends React.Component{
    // Initialise state
    constructor() {
        super();
        this.state = {
            indexDown:0,
            indexUp:0,
            left:"lorem ipsum dolor sit amet, consectetur adipiscing elit,",
            //left:"lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.",
            //keystroke: Array(462).fill(0),
            keystroke: Array(112).fill(0),
            timings:[0],
        };

        this.keyDown = this.keyDown.bind(this)
        this.keyUp = this.keyUp.bind(this)
        this.done = this.done.bind(this)
    }

    // Functions
    done(keystroke){
        let timings = this.state.timings
        let keys = this.state.keys

        for (let i=1; i<keystroke.length;i++) {
            let time = keystroke[i]-keystroke[i-1]
            timings.push(time)
        }

        console.log(timings)
        console.log(keys)
        this.setState({ timings: timings })
    }

    keyDown(event){

        let text = this.state.left
        let indexDown = this.state.indexDown
        let keystroke = this.state.keystroke


        let typed = String.fromCharCode(event.keyCode)
        console.log("Key pressed", typed)

        if (typed == "\b") {
            this.setState({ indexDown:indexDown-1 })
        } else {
            keystroke[2*indexDown] = Date.now()

            this.setState({ indexDown:indexDown+1 })
            this.setState({ keystroke: keystroke })
        }

    }

    keyUp(event){
        let text = this.state.left
        let indexUp = this.state.indexUp
        let keystroke = this.state.keystroke

        if (indexUp <= ((keystroke.length -2)/2)) {
            let typed = String.fromCharCode(event.keyCode)

            if (typed == "\b") {
                this.setState({ indexUp:indexUp-1 })
            } else {
                keystroke[2*indexUp+1] = Date.now()

                this.setState({ indexUp:indexUp+1 })
                this.setState({ keystroke: keystroke })
            }

            //console.log(keystroke)
        } else {
            console.log("Done typing")
            this.done(keystroke)
        }

    }

    // UI
    render(){
        return(
            <div className="w-screen flex h-screen">
                <div className="m-auto w-1/2 h-3/5">
                    <p>Please type the following extract:</p>
                    <p><span>{this.state.left}</span></p>
                    <input type="text" name="" id="" placeholder="Start typing..." onKeyDown={this.keyDown} onKeyUp={this.keyUp} />
                </div>

            </div>
        )
    }
}

export default TypePage
