import React from "react"

class TypePage extends React.Component{
    constructor() {
        super();
        this.state = {
            done:"",
            wrong:"",
            left:"lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.",
            keystroke: Array(426).fill(0),
            next:0
        };
        this.handleChange = this.handleChange.bind(this);
        this.keyDown = this.keyDown.bind(this)
        this.keyUp = this.keyUp.bind(this)
    }


    handleChange(event) {

    }

    keyDown(event){
        console.log("Key pressed", event.code, )
        let text = this.state.left
        let index = this.state.next
        let typed = event.target.value
        console.log(typed, text[index])

        if (typed != text[index]) {
            console.log("Mistake made")
        }

        // if (event.code != ("Key"+(text[index]).toUpperCase())) {
        //     console.log("mistake made")
        // }
    }

    keyUp(event){
        console.log("Key released", event.code)
    }

    render(){
        return(
            <div className="w-screen flex h-screen">
                <div className="m-auto w-1/2 h-3/5">
                    <p>Please type the following extract:</p>
                    <p><span>{this.state.done}</span><span>{this.state.wrong}</span><span>{this.state.left}</span></p>
                    <p>{this.state.keystroke}</p>
                    <input type="text" name="" id="" placeholder="Start typing..." onChange={this.handleChange} onKeyDown={this.keyDown} onKeyUp={this.keyUp} />
                </div>

            </div>
        )
    }
}

export default TypePage
